/*Moore,Douglas.OneLineWordReverser*/namespace Rev{internal class Program{static void Main(string[]args){Console.WriteLine("TypeTheStringToReverse");string cstring=Convert.ToString(Console.ReadLine());string rstring="";Console.WriteLine(Reverser(cstring,rstring));static string Reverser(string cstring,string rstring){for(int i=cstring.Length-1;0<=i;i--){rstring+=(cstring[i]);}return rstring;}}}}